/*global _:false, Handlebars:false, ZeroClipboard:false */
'use strict';

Handlebars.registerHelper('inc', function (value, options) {
	return parseInt(value, 10) + 1;
});

$(function() {
	var maxSlidesCount = 14,
		slidesDefaultSettings = {
			slides: [{
				time: 3,
				backgroundColor: '2ecc71',
				image: 'data:image/png;base64,',
				textColor: 'fff',
				text: 'Упростите судебно-исковую деятельность с Casebook',
				buttonText: 'Попробовать',
				buttonTextColor: 'fff',
				buttonBackgroundColor: '27ae60'
			}, {
				time: 3,
				backgroundColor: 'e74c3c',
				image: 'data:image/png;base64,',
				textColor: 'fff',
				text: 'Следите за изменениями в структуре компании',
				buttonText: 'Попробовать',
				buttonTextColor: 'fff',
				buttonBackgroundColor: 'c0392b'
			}, {
				time: 3,
				backgroundColor: '3498db',
				image: 'data:image/png;base64,',
				textColor: 'fff',
				text: 'Мониторинг судебных событий по делу',
				buttonText: 'Попробовать',
				buttonTextColor: 'fff',
				buttonBackgroundColor: '2980b9'
			}, {
				time: 3,
				backgroundColor: 'f39c12',
				image: 'data:image/png;base64,',
				textColor: 'fff',
				text: 'Ваша картотека судебных дел и компаний',
				buttonText: 'Попробовать',
				buttonTextColor: 'fff',
				buttonBackgroundColor: 'e67e22'
			}]
		},
		slidesSizes = {
			sizes: [{
				width: 120,
				height: 600
			}, {
				width: 160,
				height: 600
			}, {
				width: 200,
				height: 200
			}, {
				width: 240,
				height: 400
			}, {
				width: 250,
				height: 250
			}, {
				width: 300,
				height: 250
			}, {
				width: 300,
				height: 600
			}, {
				width: 336,
				height: 280
			}, {
				width: 468,
				height: 60
			}, {
				width: 728,
				height: 90
			}, {
				width: 970,
				height: 90
			}]
		};

	window.initConstructor = function (isFirstInit) {
		var containerClass = '.js-settings-container',
			sizesClass = '.js-sizes';

		_.forEach(slidesDefaultSettings.slides, function (slide) {
			if (!slide.logo) {
				slide.logo = 'data:image/png;base64,';
			}
		});

		$('.js-slides').html(Handlebars.compile($('#slides').html())(_.extend(_.cloneDeep(slidesDefaultSettings), {
			isMaxSlidesCount: slidesDefaultSettings.slides.length >= maxSlidesCount
		})));

		if (!isFirstInit) {
			containerClass = '.js-settings-container-temp';
			sizesClass = '.js-sizes-temp';
		}

		$(containerClass).html(Handlebars.compile($('#settings').html())(_.cloneDeep(slidesDefaultSettings)));
		$(sizesClass).html(Handlebars.compile($('#sizes').html())(slidesSizes));

		_.forEach(slidesSizes.sizes, function (size) {
			$(sizesClass + ' .js-settings-container-' + size.width + 'x' + size.height)
				.html(Handlebars.compile($('#size_settings').html())(slidesDefaultSettings));
		});

		if (!isFirstInit) {
			$(containerClass).find('.js-settings:last').appendTo($('.js-settings-container'));

			$.each($(sizesClass).find('.l-row'), function (index, row) {
				$(row).find('.js-settings:last')
					.appendTo($('.js-sizes').find('.l-row').eq(index).find('.b-settings-container'));
			});

			$(containerClass).empty();
			$(sizesClass).empty();
		}

		// Copy to clipboard via flash
		ZeroClipboard.destroy();

		var client = new ZeroClipboard($('.js-slide--copy').get(0));

		client.on('ready', function (event) {
			client.on('copy', function (event) {
				var $textarea = $(event.target).closest('.b-title').next().find('textarea');

				$textarea.select();
				event.clipboardData.setData('text/plain',$textarea.val());
			});
		});
	};

	window.stopAnimation = function () {
		if (window.animationIntervals) {
			for (var i = 0; i < window.animationIntervals.length; i++) {
				clearTimeout(window.animationIntervals[i]);
			}
		}

		$('.js-banner-container').empty();
		$('.js-textarea--source').val('');
		$('.js-button--generate_slide').prop('disabled', true);
	};

	window.initConstructor(true);
	$('body').scrollTop(0);

	// Events
	$('.l-container').on('click', '.js-slide', function (e) {
		var $item = $(e.currentTarget),
			activeClass = 'b-slide--active',
			hiddenClass = 'g-hidden';

		$('.js-slide').removeClass(activeClass);
		$item.addClass(activeClass);

		$('.js-settings').addClass(hiddenClass);
		$('.js-settings--' + $item.index()).removeClass(hiddenClass);
	});

	$('.l-container').on('click', '.js-slide--add', function () {
		if (slidesDefaultSettings.slides.length < maxSlidesCount) {
			var activeIndex = $('.b-slide--active').index();

			window.stopAnimation();

			slidesDefaultSettings.slides.push({
				time: 3,
				backgroundColor: 'aaa',
				image: '',
				textColor: 'fff',
				text: '',
				buttonText: 'Попробовать',
				buttonTextColor: 'fff',
				buttonBackgroundColor: '909090'
			});

			window.initConstructor();
			$('.js-slide').eq(activeIndex).trigger('click');
			$('.js-slide--remove').toggleClass('g-hidden', slidesDefaultSettings.slides.length <= 1);
		}
	});

	$('.l-container').on('click', '.js-slide--remove', function (e) {
		var slidesCount = slidesDefaultSettings.slides.length;

		if (slidesCount > 1) {
			var activeIndex = parseInt($(e.currentTarget).data('index'), 10),
				slidesClasses = '';

			window.stopAnimation();
			slidesDefaultSettings.slides.splice(activeIndex, 1);
			$('.js-settings--' + activeIndex).remove();
			$('.b-slide--active').remove();

			for (var i = 0; i < slidesCount; i++) {
				slidesClasses += ' js-settings--' + i;
			}

			$.each($('.b-settings-container, .js-settings-container'), function (index, container) {
				var $settings = $(container).children();

				$settings.removeClass($.trim(slidesClasses));

				$.each($settings, function (index, settings) {
					$(settings)
						.addClass('js-settings--' + index)
						.data('index', index)
						.find('.js-file')
						.data('index', index)
						.end()
						.find('.js-slide--remove')
						.data('index', index);
				});
			});

			$('.js-slides').html(Handlebars.compile($('#slides').html())(_.extend(_.cloneDeep(slidesDefaultSettings), {
				isMaxSlidesCount: slidesDefaultSettings.slides.length >= maxSlidesCount
			})));

			$('.js-slide')
				.eq(activeIndex - (activeIndex === slidesDefaultSettings.slides.length ? 1 : 0))
				.trigger('click');

			$('.js-slide--remove').toggleClass('g-hidden', slidesDefaultSettings.slides.length <= 1);
		}
	});

	$('.l-container').on('change', '.js-file', function (e) {
		var $file = $(e.currentTarget),
			file = $file[0].files[0],
			fr = new FileReader(file),
			index = parseInt($file.data('index'), 10),
			type = $file.data('type');

		fr.onload = function () {
			slidesDefaultSettings.slides[index][type] = fr.result;
		};

		fr.readAsDataURL(file);
	});

	$('.l-container').on('click', '.js-apply', function (e, index) {
		var $link = $(e.currentTarget),
			$input = $link.prev(),
			classes = $input.get(0).className,
			classesRegExp = classes.match(/js\-[a-z_\-]+$/),
			className = classesRegExp[0] || '',
			activeSlideIndex = (typeof(index) === 'undefined') ? $('.b-slide--active').index() : index,
			$inputs = $('.js-settings--' + activeSlideIndex + ' .' + className);

		$inputs.val($inputs.eq(0).val());
	});

	$('.l-container').on('click', '.js-button--generate', function (e, options) {
		var params = {
				slides: []
			};

		if (!options) {
			_.forEach(_.range(slidesDefaultSettings.slides.length), function (i) {
				$.each($('.js-settings--' + i + ':eq(0) .js-apply'), function (index, item) {
					$(item).trigger('click', [i]);
				});
			});
		}

		_.forEach(slidesDefaultSettings.slides, function (slide, index) {
			var $slideForm = $('.js-settings-container .js-settings--' + index);

			params.slides[index] = {
				time: $.trim($slideForm.find('.js-textbox--time').val()),
				backgroundColor: $.trim($slideForm.find('.js-textbox--background_color').val()),
				logo: slidesDefaultSettings.slides[index].logo || '',
				image: slidesDefaultSettings.slides[index].image || '',
				textColor: $.trim($slideForm.find('.js-textbox--text_color').val()),
				text: $.trim($slideForm.find('.js-textarea--text').val()).replace(/\n/g, ' <br>'),
				buttonText: $.trim($slideForm.find('.js-textbox--button_text').val()),
				buttonTextColor: $.trim($slideForm.find('.js-textbox--button_text_color').val()),
				buttonBackgroundColor: $.trim($slideForm.find('.js-textbox--button_background_color').val())
			};
		});

		_.forEach(slidesSizes.sizes, function (size) {
			var sizeParams = _.cloneDeep(params);

			_.forEach(slidesDefaultSettings.slides, function (slide, index) {
				var $slideSizeForm = $('.js-settings-container-' + size.width + 'x' +
									   size.height + ' .js-settings--' + index),
					slideSizeParams = {},
					time = $.trim($slideSizeForm.find('.js-textbox--time').val()),
					backgroundColor = $.trim($slideSizeForm.find('.js-textbox--background_color').val()),
					textColor = $.trim($slideSizeForm.find('.js-textbox--text_color').val()),
					text = $.trim($slideSizeForm.find('.js-textarea--text').val()).replace(/\n/g, ' <br>'),
					buttonText = $.trim($slideSizeForm.find('.js-textbox--button_text').val()),
					buttonTextColor = $.trim($slideSizeForm.find('.js-textbox--button_text_color').val()),
					buttonBackgroundColor = $.trim($slideSizeForm.find('.js-textbox--button_background_color').val());

				if (time) {
					slideSizeParams.time = time;
				}

				if (backgroundColor) {
					slideSizeParams.backgroundColor = backgroundColor;
				}

				if (backgroundColor) {
					slideSizeParams.backgroundColor = backgroundColor;
				}

				if (textColor) {
					slideSizeParams.textColor = textColor;
				}

				if (buttonText) {
					slideSizeParams.buttonText = buttonText;
				}

				if (buttonTextColor) {
					slideSizeParams.buttonTextColor = buttonTextColor;
				}

				if (buttonBackgroundColor) {
					slideSizeParams.buttonBackgroundColor = buttonBackgroundColor;
				}

				sizeParams.slides[index] = _.extend(sizeParams.slides[index], slideSizeParams);
			});

			var source = Handlebars.compile($('#banner').html())(sizeParams);

			if (options && $('.js-banner-' + size.width + 'x' + size.height).children().length) {
				if ((options.width === size.width) && (options.height === size.height)) {
					$('.js-banner-' + size.width + 'x' + size.height)
						.children()
						.eq(options.index)
						.after($('<div/>').html(source).children().eq(options.index))
						.remove();

					$('.js-settings-container-' + size.width + 'x' + size.height + ' .js-textarea--source')
						.val('<style>' + $('#banner_style').html() + '</style>' +
							 $('.js-banner-' + size.width + 'x' + size.height).parent().html() +
							 '<script>' + $('#banner_script').html() + '</' + 'script>')
						.scrollTop(0);
				}

				$('.js-banner-' + size.width + 'x' + size.height)
					.children()
					.css('display', 'none')
					.eq(0)
					.css('display', 'block');
			} else {
				$('.js-banner-' + size.width + 'x' + size.height).html(source);
				$('.js-settings-container-' + size.width + 'x' + size.height + ' .js-textarea--source')
					.val('<style>' + $('#banner_style').html() + '</style>' +
						 $('.js-banner-' + size.width + 'x' + size.height).parent().html() +
						 '<script>' + $('#banner_script').html() + '</' + 'script>')
					.scrollTop(0);
			}
		});

		if (window.animationIntervals) {
			for (var i = 0; i < window.animationIntervals.length; i++) {
				clearTimeout(window.animationIntervals[i]);
			}
		}

		window.startAnimation();
		$('.js-button--generate_slide').prop('disabled', false);
	});

	$('.l-container').on('click', '.js-button--generate_slide', function (e) {
		var $button = $(e.currentTarget),
			$settings = $button.closest('.js-settings'),
			index = parseInt($settings.data('index'), 10),
			$container = $settings.parent(),
			width = parseInt($container.data('width'), 10),
			height = parseInt($container.data('height'), 10);

		$('.js-button--generate').trigger('click', [{
			width: width,
			height: height,
			index: index
		}]);
	});

	$('.l-container').on('click', '.js-slide--copy', function (e) {
		$(e.currentTarget).closest('.b-title').next().find('textarea').select();

		try {
			document.execCommand('copy');
		} catch (e) {}
	});
});
